    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        if (!jtfName.getText().isEmpty()) {
            linkedHashSet.add(jtfName.getText());
            jtaList.setText(linkedHashSet.toString());
            jtfName.setText("");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jbResultActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbResultActionPerformed
        try {
            int startNum = Integer.valueOf(jtfMin.getText());
            int endNum = Integer.valueOf(jtfMax.getText()) + 1;
            Lottery lottery = new Lottery();
            result.setText(lottery.getResult(startNum, endNum).toString());
        } catch (Exception e) {
            JOptionPane.showMessageDialog(new JFrame(), "請輸入正確的數字 !\n最小數字不小於0", "注意", JOptionPane.INFORMATION_MESSAGE);
        }
    }//GEN-LAST:event_jbResultActionPerformed

    private void jbResult1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbResult1ActionPerformed
        Lottery lotteryList = new Lottery(linkedHashSet);
        result.setText(lotteryList.getResult());
    }//GEN-LAST:event_jbResult1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        linkedHashSet.remove(jtfName.getText());
        jtaList.setText(linkedHashSet.toString());
        jtfName.setText("");
    }//GEN-LAST:event_jButton2ActionPerformed

